function changetab(id){var tab=$("#"+id+" .titlebar ul li");var ulObj=$("#"+id+" .yylist");tab.mouseover(function(){var index=$(this).index();ulObj.eq(index).show().siblings("ul").hide();tab.eq(index).addClass("selected").siblings("li").removeClass("selected");})}
function showdown(name){var tab=$("#"+name+"  li");tab.mouseover(function(){var index=$(this).index();tab.eq(index).addClass("selected").siblings("li").removeClass("selected");})
tab.mouseout(function(){var index=$(this).index();tab.eq(index).removeClass("selected");})}
function changetabs(li,obj1,obj2){var tab=$(".title ul li");var ulObj1=$(".zjyylist .tjlist");var ulObj2=$(".imglist li");tab.mouseover(function(){var index=$(this).index();tab.eq(index).addClass("selected").siblings("li").removeClass("selected");ulObj1.eq(index).show().siblings(".zjyylist .tjlist").hide();ulObj2.eq(index).addClass("selected").siblings("li").removeClass("selected");})}
function getfocus(name,val){var obj=$(name);obj.blur(function(){var strings=obj.val();if(strings=="")
obj.val(val);else return;})
obj.focus(function(){var strings=obj.val();if(strings==val)
obj.val("");else return;})}
function getfocusTxt(id){var obj=$(id);obj.focus(function(){obj.addClass("onfocurs").removeClass("ltxt");})
obj.blur(function(){obj.removeClass("onfocurs").addClass("ltxt");})}