function scroller(){var tab=$("#AdimgList li");var length=$('#AdimgList li').size();var imgobj=$("#AdList li");var i=0;var index=0;function change(){if(i<length)
{tab.eq(i).show().siblings("li").hide();$("#AdList li").eq(i).addClass("selected").siblings("li").removeClass("selected");i++;}
else{i=0;tab.eq(i).show().siblings("li").hide();$("#AdList li").eq(i).addClass("selected").siblings("li").removeClass("selected");i++;}}
var mytimer=setInterval(change,2000);imgobj.mouseover(function(){clearInterval(mytimer);var index=$(this).index();i=index;tab.eq(i).show().siblings("li").hide();imgobj.eq(i).addClass("selected").siblings("li").removeClass("selected");})
imgobj.mouseout(function(){mytimer=setInterval(change,2000);})}